from flask import Flask
from flask_sqlalchemy import SQLAlchemy
from flask_bcrypt import Bcrypt
from flask_login import LoginManager
from flask_mail import Mail
from estoywyh.config import Config



app = Flask(__name__)
app.config['SECRET_KEY'] = 'wyhxhzzy999'
app.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///site.db'
db = SQLAlchemy(app)
bcrypt = Bcrypt()
login_manager = LoginManager()
login_manager.login_view = 'users.login'
login_manager.login_message_category = 'info'
mail = Mail()
db.init_app(app)



def create_app(config_class=Config):

    app.config.from_object(Config)
    
    bcrypt.init_app(app)
    login_manager.init_app(app)
    mail.init_app(app)

    from estoywyh.users.routes import users
    from estoywyh.posts.routes import posts
    from estoywyh.main.routes import main
    from estoywyh.errors.handlers import errors
    app.register_blueprint(users)
    app.register_blueprint(posts)
    app.register_blueprint(main)
    app.register_blueprint(errors)



    return app
